package ch04

import arrow.core.Eval
import arrow.core.fix
import arrow.core.monad
import arrow.typeclasses.binding
import p
import java.math.BigInteger

// 4.6 The Eval Monad - eval is a monad allowing us to abstract over models of evaluation
// eager lazy, memoized

fun evals() {
  val just = Eval.just(1).p() // same as now
  val always = Eval.always {
    Thread.sleep(1000)
    "Always"
  }
  val now = Eval.now(1)
  val later = Eval.later {
    Thread.sleep(1000)
    "Later"
  }

  "Calling always twice".p() // Lazy, not memoized
  always.value().p()
  always.value().p()

  "Calling now twice".p() // eager, memoized
  now.value().p()
  now.value().p()

  "Calling later twice".p() // lazy, memoized
  later.value().p()
  later.value().p()
}

fun asAMonad() {
  val greetings = Eval.always { "Step1".p(); "Hello" }
    .map { "Step2".p(); "$it world" }
  greetings.value().p() // s1 s2 Hello World

  val monad = Eval.monad().binding {
    Eval.now(40).bind() + Eval.always { "calculating".p(); 3 }.bind()
  }

  monad.p()
  monad.fix().value().p() // calculating
  monad.fix().value().p() // calculating

  val mem = Eval.always { "s1".p(); "The cat" }
    .map { "s2".p(); "$it sat" }
    .memoize()
    .map { "s3".p(); "$it here." }

  mem.value().p() // s1 s2 s3 the cat sat here
  mem.value().p() // s3 the cat sat here
}

fun trampoliningAndEvalDefer() {
  // evals map and flatmap methods are trampolined - can nest calls to map and flatmap without consuming stack frames
  // "stack safety"

  fun factorial(n: BigInteger): BigInteger = if (n.equals(BigInteger.ONE)) n else n * factorial(n.minus(BigInteger.ONE))
  //  factorial(50000.toBigInteger()) // stack overflow
  // try something better?
  fun f2(n: BigInteger): Eval<BigInteger> = if (n.equals(BigInteger.ONE)) Eval.now(n) else f2(n.minus(BigInteger.ONE)).map { it * n }

  //  f2(50000.toBigInteger()) // stack overflow again - recursive calls to f2 before start working with eval map, thus use
  // Eval.defer - takes existing instance of eval and defers evaluation. Defer is tramolined like map and flatmap to make
  // op stack safe
  fun f3(n: BigInteger): Eval<BigInteger> = if (n.equals(BigInteger.ONE)) Eval.now(n)
  else Eval.defer { f3(n.minus(BigInteger.ONE)).map { it * n } }
  f3(50000.toBigInteger()).value().p() // 


  // Eval is useful to enforce stack safety working with large computations and data structures
  // Trampolining is not free, avoids consuming stack by creating a chain of function objects on heap, still limits but
  // bounded by size of the heap rather than stack
}

