fun saferFoldingUsingEval() {
  fun <A, B> foldRight(a: List<A>, acc: B, fn: (A, B) -> B): B = when (a) {
    a.first() -> fn(a.first(), foldRight(listOf(a.last()), acc, fn))
  /*case Nil*/else -> acc
